function updateContent(languageCode) {
    var texts = languageText[languageCode] || languageText['zh-CN'];
    var randomIndex = Math.floor(Math.random() * texts['wordings'].length);
    var selectedWording = texts['wordings'][randomIndex];

    document.getElementById('hero-paragraph').innerText = selectedWording;
    document.getElementById('hero-logo').innerText = texts['hero_logo'];
    document.getElementById('hero-title').innerText = texts['hero_title'];
    document.getElementById('button-download').innerText = texts['button_download'];
	document.getElementById('hero-supported1').innerText = texts['hero_supported1'];
	document.getElementById('hero-supported2').innerText = texts['hero_supported2'];
	document.getElementById('hero-supported3').innerText = texts['hero_supported3'];
	document.getElementById('hero-supported4').innerText = texts['hero_supported4'];
	////--
	document.getElementById('hero-c1').innerText = texts['hero_c1'];
	document.getElementById('hero-c2').innerText = texts['hero_c2'];
	document.getElementById('hero-c3').innerText = texts['hero_c3'];
	document.getElementById('hero-c4').innerText = texts['hero_c4'];
	////--
	document.getElementById('hero-call').innerText = texts['hero_call'];
	document.getElementById('hero-my').innerText = texts['hero_my'];
	document.getElementById('hero-questions').innerText = texts['hero_questions'];
	document.getElementById('hero-help').innerText = texts['hero_help'];
}

var languageSelect = document.getElementById('language-select');
languageSelect.addEventListener('change', function() {
    var selectedLanguage = languageSelect.value;
    updateContent(selectedLanguage);
});

updateContent('zh-CN');

// 设置定时器，每隔3000毫秒调用一次updateContent函数
setInterval(function() {
    var selectedLanguage = languageSelect.value || 'zh-CN';
    updateContent(selectedLanguage);
}, 3000);
